mapper.insert:
INSERT INTO market_board
	(
		boardnum
		, memberid
		, title
		, contents
		, category
	)
	VALUES
	(
		market_board_seq.NEXTVAL
		, #{memberid}
		, #{title}
		, #{contents}
		, #{category}
	)

mapper.delete:
DELETE FROM market_board
WHERE
	boardnum = #{boardnum}
	AND
	memberid = #{memberid}

mapper.update:
UPDATE market_board
SET
	soldout = #{soldout}
	, buyerid = #{buyerid}
WHERE
	boardnum = #{boardnum}

mapper.insert:
INSERT INTO market_reply
	(
		replynum
		, boardnum
		, memberid
		, replytext
	)
	VALUES
	(
		market_reply_seq.NEXTVAL
		, #{boardnum}
		, #{memberid}
		, #{replytext}
	)
